package Util;

public class Fecha {
	private Integer dia;
	private Integer mes;
	private Integer año;
	public Fecha() {
		// TODO Auto-generated constructor stub
	}
	public Integer getDia() {
		return dia;
	}
	public void setDia(Integer dia) {
		this.dia = dia;
	}
	public Integer getMes() {
		return mes;
	}
	public void setMes(Integer mes) {
		this.mes = mes;
	}
	public Integer getAño() {
		return año;
	}
	public void setAño(Integer año) {
		this.año = año;
	}

}
